mapper.update:
UPDATE bill
SET is_delete = 1 And is_pay = 0
WHERE User_id = #{userId}
  AND Room_id = #{roomId};

mapper.select:
SELECT *
FROM bill
WHERE id = #{id}
  AND is_pay = 1
  AND is_delete = 0;

SELECT SUM(tmp.count * ri.item_price) + r.price * DATEDIFF(NOW(), ur.create_time)
FROM room_item ri, user_room ur, room r, (
    SELECT id, count
    FROM (

            SELECT #{item.itemId} AS id, #{item.count} AS count

    ) t
) tmp
WHERE ri.Room_id = #{roomId}
  AND ur.room_id = #{roomId}
  AND ur.user_id = #{userId}
  AND r.id = #{roomId}
  AND tmp.id = ri.id
GROUP BY ur.create_time

mapper.update:
UPDATE room_item
SET item_num  = item_num - #{count},
    item_sell = item_sell + #{count}
WHERE id = #{itemId}

mapper.select:
SELECT ri.id as itemId, ri.item_name as itemName, ri.item_price as price, tmp.count AS count, tmp.count * ri.item_price AS totalPrice FROM room_item ri LEFT JOIN ( SELECT id, count FROM (
SELECT #{item.itemId} AS id, #{item.count} AS count
) t ) tmp ON tmp.id = ri.id
