mapper.select:
SELECT *
FROM room
WHERE room_number = #{roomNumber}
  AND status = 0
  AND is_delete = 0;

mapper.update:
UPDATE room SET room_number = #{roomNumber}, room_type = #{roomType}, telephone = #{telephone},
                price = #{price}, status = #{status}
WHERE id = #{id} AND is_delete = 0;

mapper.select:
SELECT *
FROM room
WHERE id = #{id}
  AND status = 0
  AND is_delete = 0;

SELECT *
FROM room
WHERE id = #{id}
  AND status = 1
  AND is_delete = 0;
